script:
function( avar, anobj ) {
    $( '.set_value_class', $(avar) ).on('click', function () {
      var but = $('.set_value_class', $(avar) );
      but.addClass('ui-disabled');
      var cmd = anobj.cmd_name; 
      nedm.send_command( { cmd_name : cmd, 
                           arguments : [ parseFloat($('[type=text]', $(avar)).val()) ],
                           timeout : 10000,
                           callback: function(o) { 
                              but.removeClass('ui-disabled');
                           } 
                         });
    });
}
