script:
$('#submit_set_{{{cmd_name}}}').on('click', function () {
  var but = $('#submit_set_{{{cmd_name}}}');
  but.addClass('ui-disabled');
  var cmd = '{{{cmd_name}}}';
  nedm.send_command( { cmd_name : cmd, 
                       arguments : [ parseFloat($('#set_{{{cmd_name}}}_text').val()) ],
                       timeout : 10000,
                       callback: function(o) { 
                          but.removeClass('ui-disabled');
                       } 
                     });
});
